import { Checkbox, CheckboxGroup } from '@chakra-ui/react'
import { useEffect, useState } from 'react'
import { useSearchParams } from 'react-router-dom'

function SideNavbar() {

    const [searchParam,setSearchParam] = useSearchParams()

    console.log('params',searchParam.getAll("category"))

    const [oldvalue,setValue] = useState([])

    function handleChange(e){
        const storeTarget = e.target.value
        console.log(oldvalue)
        const newArray = [...oldvalue]

        if(oldvalue.includes(storeTarget)){
            newArray.splice(newArray.indexOf(storeTarget),1)
        } else{
            newArray.push(storeTarget)
        }

        setValue(newArray)

    }

    useEffect(()=>{
        let params = {
            category:oldvalue
        }

        setSearchParam(params)
    },[oldvalue])

    return (

        <div>
            <h2 style={{fontSize:"25px"}}>Filter</h2>
            <div>
                <Checkbox colorScheme='green' spacing='1rem' value={"Man"} onChange={handleChange}>Man</Checkbox>
            </div>
            <div>
                <Checkbox colorScheme='green' spacing='1rem' value={"Women"} onChange={handleChange}>
                    Women
                </Checkbox>
            </div>
            <div>
                <Checkbox colorScheme='green' spacing='1rem' value={"Kids"} onChange={handleChange}>
                    Kids
                </Checkbox>
            </div>
        </div>



    )
}

export default SideNavbar
