import { useState } from "react"
import "./Admin.css"


const initState = {
    image:'',
    name:'',
    price:'',
    brand:'',
    category:''


}





function Admin(){
    const [state,setState] = useState(initState)

    function handleChange(e){
        const {value,name} = e.target
        console.log(value,name);
    }

    return(

        <form>
            <input type="text" name="image" placeholder="Image Url" value={state.image} onChange={(e)=>handleChange()}/>
            <input type="text" name="name" placeholder="Tittle"  value={state.name} onChange={(e)=>handleChange()}/>
            <input type="text" name="price" placeholder="Price"  value={state.price} onChange={(e)=>handleChange()}/>
            <input type="text" name="brand" placeholder="Brand"  value={state.brand} onChange={(e)=>handleChange()}/>
            <select name="category"  value={state.category} onChange={(e)=>handleChange()}>
                <option value="">Select</option>
                <option value="man">Men</option>
                <option value="women">Women</option>
            </select>
            
        </form>
        
    )
}




export default Admin
